from http.client import GONE
import streamlit as st
import yfinance as yf
import pandas as pd
import numpy as np
import matplotlib.pyplot as plt
import seaborn as sns
import plotly.express as px
import datetime 
from datetime import date, timedelta
from statsmodels.tsa.seasonal import seasonal_decompose
import statsmodels.api as sm
from statsmodels.tsa.stattools import adfuller
import plotly.graph_objects as go
import plotly.express as px




# title 
app_name = 'Stock Market Forecasting App'
st.title(app_name)
st.subheader('This app is created by ADNAN-KHOKHAR FINANCIAL MARKETS EXPERT to forecast the stock market price of the selected company')
# add an image from online resource
st.image("data:image/jpeg;base64,")

# take input from the user of app about start and end date

# sidebar 
st.sidebar.header('select the parameters from below')

start_date = st.sidebar.date_input('start date', date(2020, 1, 1))
end_date = st.sidebar.date_input('end date', date(2024, 5, 7))
# add ticker symbol list
ticker_list = ["AAPL", "JPY=X" , "BTC-USD" , "GOLD", "CAD=X", "GOOGL", "TSLA", "EURUSD=X","GBPUSD=X","AUDUSD=X","NZDUSD=X" , "ADA-USD", "NEAR-USD", "RUNE-USD", "ARB-USD", "WIF-USD", "PEPE-USD", "SHIB-USD", "WLD-USD", "MATIC-USD", "SUI-USD", "RNDR-USD", ]
ticker = st.sidebar.selectbox('select the company', ticker_list)

# fetch data from user inputs using yfinance library

data = yf.download(ticker,start=start_date, end=end_date)
# add date as a column to the dataframe
data.insert(0,"Date",data.index, True)
data.reset_index(drop=True , inplace=True)
st.write('data from', start_date, 'to', end_date)
st.write(data)

#plot the data
st.header('Dat Visualization')
st.subheader('Plot of the data')
st.write("**Note:** Select your spacific date range on the sidebar,or zoom in on the plot and select your spacific column")
fig = px.line(data, x='Date',y=data.columns, title='closing price of stock', width=800, height=300)
st.plotly_chart(fig)

#add a select box to select column from data

column = st.selectbox('Select the column to be used for forecasting', data.columns[1:])

#subsetting the data 
data = data[['Date', column]]
st.write("Selected Data")
st.write(data)

# ADF test check stationarity
st.header('is data Stationary?')
st.write('**Note:** if p-valueis less then 0.05, then data is stationary')
st.write(adfuller(data[column])[1]<0.05)

# lets decompose the data
st.header('Decomposition of the data')
decomposition = seasonal_decompose(data[column], model='additive', period=12)
st.write(decomposition.plot())
#make the same plot in plotly
st.write("## Plotting the decomposition in plotly")
st.plotly_chart(px.line(x=data["Date"], y=decomposition.trend, title='Trend', width=800, height=300, labels={'x': 'Date', 'y': 'Price'}).update_traces(line_color='Blue'))

st.plotly_chart(px.line(x=data["Date"], y=decomposition.seasonal, title='seasonality', width=800, height=300 , labels={'x': 'Date', 'y': 'Price'}).update_traces(line_color='green'))

st.plotly_chart(px.line(x=data["Date"], y=decomposition.resid, title='Residuals', width=800, height=300, labels={'x': 'Date', 'y': 'Price'}).update_traces(line_color='Red', line_dash='dot'))

#let's run the model
# user input for three parameters of the model and seasonal order
p = st.slider('Select the value of p', 0, 5, 2)
d = st.slider('Select the value of d', 0, 5, 1)
q = st.slider('Select the value of q', 0, 5, 2)
seasonal_order = st.number_input('select the value of seasonal p', 0, 24, 12)

model = sm.tsa.statespace.SARIMAX(data[column], order=(p,d,q), seasonal_order=(p,d,q,seasonal_order))
model = model.fit()


# print model summary
st.header('Model Summary')
st.write(model.summary())
st.write("---")

# predict the future value (Forecasting)
forecast_period = st.number_input(' Select the number of days to forecast', 1, 365, 10)

# predict the future value
predictions = model.get_prediction(start=len(data), end=len(data)+forecast_period-1)
predictions = predictions.predicted_mean
# st.write(len(predic))
# add index to results dataframe as dates
predictions.index = pd.date_range(start=end_date, periods=len(predictions), freq='D')
predictions = pd.DataFrame(predictions)
predictions.insert(0, 'Date', predictions.index)
predictions.reset_index(drop=True, inplace=True)
st.write("## Predictions", predictions)
st.write("## Actual Data", data)
st.write("---")


# lets plot the data
# fig = plt.figure()
fig = go.Figure()
#add actual data to the plot 
fig.add_trace(go.Scatter(x=data["Date"], y=data[column], mode='lines', name='Actual', line=dict(color='blue')))
# add predicted data to the plot
fig.add_trace(go.Scatter(x=predictions["Date"], y=predictions["predicted_mean"], mode='lines', name='Predicted', line=dict(color='red')))
# set the title and axis lebels
fig.update_layout(title='Actul vs Predicted', xaxis_title='Date', yaxis_title='Price',width=800, height=300)
# display the pot
st.plotly_chart(fig)

#Add button to show and hide seprate block
show_plots = False
if st.button('Show Seprate Plots'):
    if not show_plots:
       st.write(px.line(x=data["Date"], y=data[column], title='Actual', width=800, height=300, labels={'x': 'Date', 'y': 'Price'}).update_traces(line_color='Blue'))
       st.write(px.line(x=predictions["Date"], y=predictions["predicted_mean"], title='Predicted', width=900,   height= 300, labels={'x': 'Date', 'y': 'Price'} ))
       show_plots = True
    else:
        show_plots = False 
hide_plots = False
if st.button("Hide Separate Plots"):
    if not hide_plots:
       hide_plots = True
    else:
        hide_plots = False
        
st.write("---")   

st.write("<p style='color:blue; font-weight: bold ; font-size: 50px '>Hamza Tech ", unsafe_allow_html= True)

     
        
        
        
        
        
    



